import React, { useState, useEffect } from "react";
import YouTube from "react-youtube";
import "./styles.css";
import VideoList from "./Components/VideoList.js";
import Form from "./Components/Form.js";
import { TitleContext } from "../index";
import { LinkContext } from "../index";

const App = () => {
  const [videoList, setVideoList] = useState([]);
  const [currentAccount, setCurrentAccount] = useState("");
  // const [videoLink, setVideoLink] = useState({});
  const { videoLink, setVideoLink } = useContext(LinkContext);
  // const [title, setTitle] = useState(null);
  const { title, setTitle } = useContext(TitleContext);

  const checkIfWalletIsConnected = async () => {
    const { ethereum } = window;

    if (!ethereum) {
      console.log("Make sure you have metamask!");
      return;
    } else {
      console.log("We have the ethereum object", ethereum);
    }

    const accounts = await ethereum.request({ method: "eth_accounts" });

    if (accounts.length !== 0) {
      const account = accounts[0];
      console.log("Found an authorized account:", account);
      setCurrentAccount(account);
    } else {
      console.log("No authorized account found");
    }
  };

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }

      /*
       * Fancy method to request access to account.
       */
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });

      /*
       * Boom! This should print out public address once we authorize Metamask.
       */
      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
    } catch (error) {
      console.log(error);
    }
  };

  //#endregion
  const renderNotConnectedContainer = () => (
    <button
      onClick={connectWallet}
      className="cta-button connect-wallet-button"
    >
      Connect to Wallet
    </button>
  );

  const addVideo = (video) => {
    console.log(video);
    setVideoList([video]);

    console.log("setState video", video);
  };
  //#endregion
  useEffect(() => {
    checkIfWalletIsConnected();
  }, []);

  return (
    <div className="App">
      {currentAccount === "" ? (
        renderNotConnectedContainer()
      ) : (
        <p>Wallet connected with address {currentAccount}</p>
      )}
      <Form addVideo={addVideo} />
      <VideoList videoList={videoList} />
    </div>
  );
};

export default App;
